function range(start, end, step = start &lt; end ? 1 : -1) {
    let array = [];

    if (step &gt; 0) {
      for (let i = start; i &lt;= end; i += step) array.push(i);
    } else {
      for (let i = start; i &gt;= end; i += step) array.push(i);
    }
    return array;
  }

  function sum(array) {
    let tot = 0;
    for (let value of array) {
      tot += value;
    }
    return tot;
  }

  console.log(range(1, 10))
  console.log(range(5, 2, -1));
  console.log(sum(range(1, 10)));

